import React , {Component} from 'react'

class TodoApp extends Component{

    render(){
        return(
            <div className="ToDoApp">
                <LoginComponent/>
            </div>
        )
    }
}

class LoginComponent extends Component{

    constructor(props){
        super(props);
        this.state = {
            username : 'winju',
            password : ''
        }
    }

    // usernameChangeHandler = (event)=>{
    //     console.log(event.target.value);
    //     this.setState({
    //         username : event.target.value
    //     })
    // }

    // passwordChangeHandler = (event)=>{
    //     console.log(event.target.value);
    //     this.setState({
    //         password : event.target.value
    //     })
    // }

    changeHandler = (event)=>{
        console.log(event.target.value);
        // const typeOfField = event.target.name;
        this.setState({
            [event.target.name] : event.target.value // the form element name(event.target.name) should match state elemet name
        })
    }
    render(){
        return(
            <div>
                Username:<input type="text" name="username" value={this.state.username} onChange={this.changeHandler}/>
                Password:<input type="password" name="password" value={this.state.password} onChange={this.changeHandler}/>
                <button>Login</button>
            </div>
        )
    }
}

export default TodoApp;
